import React, { useState } from "react";
import "./App.css";

const API_KEY = "e58fced4a1f4a6e05e3e26e43a400c1d"; // Replace with your OpenWeatherMap API Key

function App() {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);

  const fetchWeather = async () => {
    if (!city) return;
    const res = await fetch(
      `https://api.openweathermap.org/data/2.5/weather?q=${delhi}&appid=${e58fced4a1f4a6e05e3e26e43a400c1d}&units=metric`
    );
    const data = await res.json();
    setWeather(data);
  };

  return (
    <div className="container">
      <h1>Weather Forecast Widget</h1>
      <input
        type="text"
        placeholder="Enter city"
        value={city}
        onChange={(e) => setCity(e.target.value)}
      />
      <button onClick={fetchWeather}>Get Weather</button>
      {weather && weather.main && (
        <div className="weather-card">
          <h2>{weather.name}</h2>
          <p>{weather.weather[0].main}</p>
          <p>{weather.main.temp}Â°C</p>
        </div>
      )}
    </div>
  );
}

export default App;
